import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = "http://192.168.29.87:8000";

const DEFAULTS = {
  db_type: 'mysql',
  host: 'localhost',
  port: 3306,
  user: 'root',
  password: '',
  database: '',
};

function App() {
  const [query, setQuery] = useState('');
  const [messages, setMessages] = useState([]);
  const [rows, setRows] = useState([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(25);
  const [loading, setLoading] = useState(false);
  const [schema, setSchema] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [dbConfig, setDbConfig] = useState(null);
  const [dbForm, setDbForm] = useState(DEFAULTS);
  const [dbError, setDbError] = useState('');
  const chatBoxRef = useRef(null);

  useEffect(() => {
    if (dbConfig) {
      axios.post(`${API_URL}/schema`, { db_config: dbConfig })
        .then(res => {
          if (res.data.error) setDbError(res.data.error);
          else {
            setSchema(res.data);
            setDbError('');
          }
        })
        .catch(() => setDbError('Could not connect to database.'));
    }
  }, [dbConfig]);

  useEffect(() => {
    if (chatBoxRef.current) {
      chatBoxRef.current.scrollTop = chatBoxRef.current.scrollHeight;
    }
  }, [messages, loading]);

  const fetchPage = async (p = page, ps = pageSize, userQuery = query) => {
    if (!userQuery || !dbConfig) return;
    setLoading(true);
    try {
      const res = await axios.post(`${API_URL}/chat`, {
        query: userQuery,
        db_config: dbConfig,
        page: p,
        page_size: ps,
      });
      if (res.data && res.data.rows !== undefined) {
        setRows(res.data.rows || []);
        setTotal(res.data.total || 0);
        setPage(p);
        setPageSize(ps);
        setMessages((msgs) => [
          ...msgs,
          { type: 'bot', text: `Returned ${res.data.rows?.length || 0} rows (page ${p})`, sql: res.data.sql }
        ]);
      } else {
        setMessages((msgs) => [...msgs, { type: 'bot', text: 'No results found.' }]);
        setRows([]);
        setTotal(0);
      }
    } catch {
      setMessages((msgs) => [...msgs, { type: 'bot', text: 'Error: Could not get response from backend.' }]);
    }
    setLoading(false);
  };

  const handleSend = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setMessages((msgs) => [...msgs, { type: 'user', text: query }]);
    await fetchPage(1, pageSize, query);
    setQuery('');
  };

  const totalPages = Math.max(1, Math.ceil(total / pageSize));
  const canPrev = page > 1;
  const canNext = page < totalPages;
  const handlePrev = () => canPrev && fetchPage(page - 1, pageSize, messages.find(m => m.type==='user')?.text || '');
  const handleNext = () => canNext && fetchPage(page + 1, pageSize, messages.find(m => m.type==='user')?.text || '');
  const handlePageSizeChange = (e) => {
    const ps = Number(e.target.value) || 25;
    fetchPage(1, ps, messages.find(m => m.type==='user')?.text || '');
  };

  const handleDbFormChange = (e) => {
    const { name, value } = e.target;
    setDbForm(f => ({ ...f, [name]: name === 'port' ? Number(value) : value }));
  };

  const handleDbConnect = async (e) => {
    e.preventDefault();
    setDbError('');
    setSchema(null);
    setDbConfig(null);
    try {
      const res = await axios.post(`${API_URL}/schema`, { db_config: dbForm });
      if (res.data.error) {
        setDbError(res.data.error);
      } else {
        setSchema(res.data);
        setDbConfig(dbForm);
        setDbError('');
      }
    } catch {
      setDbError('Could not connect to database.');
    }
  };

  if (!dbConfig || !schema) {
    return (
      <div className="db-form-container">
        <form className="db-form" onSubmit={handleDbConnect}>
          <h2>Connect to Database</h2>
          <label>
            DB Type
            <select name="db_type" value={dbForm.db_type} onChange={handleDbFormChange}>
              <option value="mysql">MySQL</option>
              <option value="postgresql">PostgreSQL</option>
              <option value="sqlite">SQLite</option>
              <option value="mssql">MSSQL</option>
            </select>
          </label>
          {dbForm.db_type !== 'sqlite' && <>
            <label>
              Host
              <input name="host" value={dbForm.host} onChange={handleDbFormChange} required />
            </label>
            <label>
              Port
              <input name="port" type="number" value={dbForm.port} onChange={handleDbFormChange} required />
            </label>
            <label>
              User
              <input name="user" value={dbForm.user} onChange={handleDbFormChange} required />
            </label>
            <label>
              Password
              <input name="password" type="password" value={dbForm.password} onChange={handleDbFormChange} />
            </label>
            <label>
              Database
              <input name="database" value={dbForm.database} onChange={handleDbFormChange} required />
            </label>
          </>}
          {dbForm.db_type === 'sqlite' && <>
            <label>
              Database File Path
              <input name="database" value={dbForm.database} onChange={handleDbFormChange} required />
            </label>
          </>}
          <button type="submit">Connect</button>
          {dbError && <div className="db-error">{dbError}</div>}
        </form>
      </div>
    );
  }

  return (
    <div className="app-container">
      <button className="sidebar-toggle" onClick={() => setSidebarOpen(v => !v)}>
        {sidebarOpen ? '⏴' : '⏵'}
      </button>
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <h2>Database Schema</h2>
        {schema ? (
          <div className="schema-list">
            {Object.entries(schema).map(([table, columns]) => (
              <div key={table} className="schema-table">
                <div className="schema-table-name">{table}</div>
                <ul className="schema-columns">
                  {columns.map(col => (
                    <li key={col}>{col}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        ) : (
          <div className="schema-loading">Loading schema...</div>
        )}
      </div>
      <h1>NL-SQL Chat</h1>
      <div className="chat-box" ref={chatBoxRef}>
        {messages.map((msg, idx) => (
          <div key={idx} className={`message ${msg.type}`}>
            {msg.type === 'bot' && (
              <div className="avatar bot" title="Bot">🤖</div>
            )}
            <div className={`bubble ${msg.type}`}>
              {typeof msg.text === 'string' ? msg.text : <pre>{JSON.stringify(msg.text, null, 2)}</pre>}
              {msg.sql && <details><summary>SQL</summary><pre>{msg.sql}</pre></details>}
            </div>
            {msg.type === 'user' && (
              <div className="avatar user" title="You">U</div>
            )}
          </div>
        ))}
        {loading && (
          <div className="message bot">
            <div className="avatar bot" title="Bot">🤖</div>
            <div className="bubble bot">Loading...</div>
          </div>
        )}
      </div>
      {/* Results table */}
      {!!rows && rows.length > 0 && (
        <div className="results">
          <div className="results-header">
            <div>Rows: {rows.length} / {total}</div>
            <div>
              Page size:
              <select value={pageSize} onChange={handlePageSizeChange}>
                <option value={10}>10</option>
                <option value={25}>25</option>
                <option value={50}>50</option>
                <option value={100}>100</option>
              </select>
            </div>
          </div>
          <div className="table-wrapper">
            <table className="data-table">
              <thead>
                <tr>
                  {Object.keys(rows[0]).map((col) => (
                    <th key={col}>{col}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {rows.map((r, i) => (
                  <tr key={i}>
                    {Object.keys(rows[0]).map((col) => (
                      <td key={col}>{String(r[col] ?? '')}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="pagination">
            <button onClick={handlePrev} disabled={!canPrev}>Prev</button>
            <span>Page {page} of {totalPages}</span>
            <button onClick={handleNext} disabled={!canNext}>Next</button>
          </div>
        </div>
      )}
      <form className="input-form" onSubmit={handleSend}>
        <input
          type="text"
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Ask a question about your data..."
          disabled={loading}
        />
        <button type="submit" disabled={loading || !query.trim()}>Send</button>
      </form>
    </div>
  );
}

export default App;
