import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    setMessages((msgs) => [...msgs, { type: 'user', text: query }]);
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:8000/chat', { query });
      setMessages((msgs) => [
        ...msgs,
        { type: 'bot', text: res.data.response }
      ]);
    } catch (err) {
      setMessages((msgs) => [
        ...msgs,
        { type: 'bot', text: 'Error: Could not get response from backend.' }
      ]);
    }
    setQuery('');
    setLoading(false);
  };

  return (
    <div className="app-container">
      <h1>SQL-JSON Chat</h1>
      <div className="chat-box">
        {messages.map((msg, idx) => (
          <div key={idx} className={`message ${msg.type}`}>
            <span>{msg.type === 'user' ? 'You: ' : 'Bot: '}</span>
            {typeof msg.text === 'string' ? msg.text : <pre>{JSON.stringify(msg.text, null, 2)}</pre>}
          </div>
        ))}
        {loading && <div className="message bot">Bot: <span>Loading...</span></div>}
      </div>
      <form className="input-form" onSubmit={handleSend}>
        <input
          type="text"
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Ask a question about your data..."
          disabled={loading}
        />
        <button type="submit" disabled={loading || !query.trim()}>Send</button>
      </form>
    </div>
  );
}

export default App;
